{
    "legend": "Contact (generated by contact-form.eson)",
    "fields": [
      {
          "label": "Name",
          "model": "model.contact.name",
          "type": "text",
          "required": true
      },
      {
          "label": "Email",
          "model": "model.contact.email",
          "type": "email",
          "required": true,
          "prefix": {
              "glyphicon": "envelope"
          }
      },
      {
          "label": "Fone",
          "model": "model.contact.phone",
          "type": "text",
          "prefix":{
              "glyphicon":"phone-alt"
          }
      },
      {
          "label": "Age",
          "model": "model.contact.age",
          "type": "number",
          "required": true
      },
      {
          "label": "Parent's Email",
          "model": "model.contact.parentEmail",
          "type": "email",
          "required": false,
          "prefix": {
              "glyphicon": "envelope"
          },
          "showOnlyIf": "model.contact.age != undefined && model.contact.age < 18"
      }
    ]
}